import { useEffect, useState } from 'react';



function App() {

  const [counter, setCounter] = useState(0)
  const [counter2, setCounter2] = useState(0)



  // useEffect(() => {
  //   console.log('counter tiene el valor de:', counter2)
  // }, [counter2])

  useEffect(() => {
    const updateWidth = () => {
      const width = document.body.clientWidth
      console.log(`updateWidth con ${width}`)
    }
    // Actualizaremos el width al montar el componente
    updateWidth()
    // Nos suscribimos al evento resize de window
    window.addEventListener("resize", updateWidth)
  }, [])

  console.log('se ejecuta fuera del efecto')

  const handleOnclick = () => setCounter(prev => prev + 1)

  const handleOnclick2 = () => setCounter2(prev => prev + 1)

  return (
    <div>
      <button onClick={handleOnclick}>
         counter {counter}
      </button>
      <br/>     <br/>
      <button onClick={handleOnclick2}>
      counter2 {counter2}
      </button>
    </div>
  );
}

export default App;
