import { useEffect, useState } from 'react';


const Timmer = () => {
  const [laps, setLaps] = useState(0)
  const [timeInSeconds, setTimeInSeconds] = useState(0)

  useEffect(() => {
    setTimeInSeconds(0)

    setInterval(() => {
      setTimeInSeconds(prev => prev + 1)
      console.log('test')
    }, 1000)

  }, [laps])

  return (
    <>
    <button onClick={() => setLaps(prev => prev + 1) }>
        Laps
    </button>
    <h1>{timeInSeconds}</h1>
    </>
  )
}

function App() {



  return (
    <div>
    <Timmer />
    </div>
  );
}

export default App;
