import { MinusIcon, PlusIcon, ShoppingCartIcon } from 'icons'
import { useState } from 'react'

const ItemCount = ({ stock, initial, onAdd }) => {
  const [quantity, setQuantity] = useState(initial)

  const increment = () => {
    if (quantity >= stock) return
    setQuantity(prev => prev + 1)
  }

  const decrement = () => {
    if (quantity <= 1) return
    setQuantity(prev => prev - 1)
  }

  return (
    <div className='max-w-[500px] flex items-center gap-2'>
      <div className='flex justify-between w-full '>
        <button
          className='w-[24px] h-[24px] bg-indigo-100 flex justify-center items-center hover:bg-indigo-300 p-0.5'
          onClick={increment}
        >
          <PlusIcon width={20} height={20} />
        </button>
        <span>{quantity}</span>
        <button
          className='w-[24px] h-[24px] bg-indigo-100 flex justify-center items-center hover:bg-indigo-300 p-0.5'
          onClick={decrement}
        >
          <MinusIcon width={20} height={20} />
        </button>
      </div>
      <button className='bg-indigo-700 rounded-sm px-4 py-0.5 w-[300px] text-white flex gap-2 items-center justify-center' onClick={onAdd}>add to cart
        <ShoppingCartIcon width={20} />
      </button>
    </div>
  )
}

export default ItemCount
