import ItemSkeleton from 'components/Item/ItemSkeleton'
import Item from '../Item'
import { Link } from 'react-router-dom'

const ItemList = ({ products, loading }) => {
  if (loading) {
    return (
      <div className='grid gap-5 grid-cols-4'>
        {[...Array(10).keys()].map((index) => <ItemSkeleton key={index} />)}
      </div>
    )
  }

  if (!products.length) {
    return (
      <div className='text-center text-xl'>
        <h3>No items with the filters applied, please perform another search</h3>
        <Link to='/' className='border- border-indigo-700'>
          Go to home
        </Link>
      </div>
    )
  }

  return (
    <div className='grid gap-5 grid-cols-4'>
      {products.map((product, index) => <Item key={product.id} {...product} />)}
    </div>
  )
}

export default ItemList
