//import ItemCount from "ItemCount"

import ItemList from "../ItemList"
import { getProducts } from "../../mock"
import { useEffect, useState } from "react"

const ItemListContainer = ({ greeting }) => {

    const [products, setProducts] = useState([])

    useEffect( () => {
        getProducts()
        .then((products) => setProducts(products))
        .catch(err => {
            console.log({err})
        })
    }, [])


    // const onAdd = (quantity) => {
    //     console.log(quantity)
    // }

    return (
        <section className="max-w-5xl mx-auto pt-[100px]">
            <h1 className="text-center mb-10 text-4xl font-extrabold text-transparent
             bg-clip-text bg-gradient-to-r from-indigo-700 to-[#a64aff]">
                E-COMMERCE
            </h1>
           <ItemList products={products} loading={false} /> 
            {/* <ItemCount stock={8} initial={1} onAdd={onAdd} /> */}
        </section>
    )
}

export default ItemListContainer
