import { useEffect, useState } from "react";

//useState

function App() {



  const [valor, setValor] = useState(0)

  const [estadoLocalStorage, setEstadoLocalStorage] = useState(null)



  useEffect(() => {
    const user = localStorage.getItem('user-login')
    setEstadoLocalStorage(JSON.parse(user))
  }, [])

  const handleChange = () => {


   setValor(prev => prev + 1)
  }


  return (
    <div>
      <button onClick={handleChange} className="bg-red-400 rounded-sm px-4 py-1">
        Change state {valor} # {estadoLocalStorage}
      </button>
    {/* <NavBar />
    <ItemListContainer greeting='Aun no hay registros' /> */}
    </div>
  );
}

export default App;
