import { MinusIcon, PlusIcon } from "icons"
import { useState } from "react"

const ItemCount = ({ stock, initial, onAdd }) => {

    const [quantity, setQuantity] = useState(initial)

    const increment = () => {
        if (quantity >= stock) return
        setQuantity(prev => prev + 1)
    }

    const decrement = () => {
        if (quantity <= 1) return
        setQuantity(prev => prev - 1)
    }

    return (
        <div className="max-w-[200px] flex flex-col items-center">
            <div className="flex justify-between w-full">
                <button className="w-[30px] h-[30px] rounded-full bg-indigo-100 flex justify-center items-center hover:bg-indigo-300" onClick={increment}>
                    <PlusIcon />
                </button>
                <span>{quantity}</span>
                <button
                    className="w-[30px] h-[30px] rounded-full bg-indigo-100 flex justify-center items-center hover:bg-indigo-300"
                    onClick={decrement}>
                    <MinusIcon />
                </button>
            </div>
            <button className="bg-indigo-700 rounded-md px-4 py-2" onClick={onAdd}>add to cart {quantity}</button>
        </div>
    )
}

export default ItemCount
